CREATE TABLE users (id SERIAL PRIMARY KEY, name VARCHAR(250) NOT NULL, email VARCHAR(250) NOT NULL UNIQUE, birth_date DATE NOT NULL, password TEXT NOT NULL);
CREATE TABLE restrictions (id SERIAL PRIMARY KEY, name VARCHAR(250) NOT NULL, description TEXT NOT NULL, icon TEXT NOT NULL);
CREATE TABLE share_types (id SERIAL PRIMARY KEY, name VARCHAR(250) NOT NULL, description TEXT NOT NULL, icon TEXT NOT NULL);
CREATE TABLE utensils (id SERIAL PRIMARY KEY, name VARCHAR(250) NOT NULL, icon TEXT NOT NULL);
CREATE TABLE homes (id SERIAL PRIMARY KEY, user_id INT NOT NULL REFERENCES users(id) ON DELETE CASCADE, address TEXT NOT NULL, city VARCHAR(250) NOT NULL, description TEXT NOT NULL, cost_day REAL NOT NULL, cost_week REAL, cost_month REAL, restriction_id INT REFERENCES restrictions(id) ON DELETE SET NULL, share_type_id INT REFERENCES share_types(id) ON DELETE SET NULL);
CREATE TABLE home_pictures (uuid UUID PRIMARY KEY DEFAULT gen_random_uuid(), home_id INT NOT NULL REFERENCES homes(id) ON DELETE CASCADE, position INT NOT NULL, path TEXT NOT NULL);
CREATE TABLE bookings (id SERIAL PRIMARY KEY, user_id INT REFERENCES users(id) ON DELETE SET NULL, home_id INT REFERENCES homes(id) ON DELETE SET NULL, from_date DATE NOT NULL, to_date DATE NOT NULL, payment_type INT NOT NULL, COST_PER_CYCLE REAL NOT NUll);
CREATE TABLE home_utensils_pivot (home_id INT NOT NULL REFERENCES homes(id) ON DELETE CASCADE, utensil_id INT NOT NULL REFERENCES utensils(id) ON DELETE CASCADE, PRIMARY KEY(home_id, utensil_id));